model.component().create("comp1", true);
model.component("comp1").geom().create("geom1", 2);
model.component("comp1").mesh().create("mesh1");

model.param().set("r_i", "0.5 [mm]", "Coax inner radius");
model.param().set("r_o", "3.43 [mm]", "Coax outer radius");
model.param().set("eps_r", "2.4", "Relative dielectric constant");
model.param().set("Z0_analytic", "(Z0_const/(2*pi*sqrt(eps_r)))*log(r_o/r_i)", "Characteristic impedance, analytic");


model.component("comp1").geom("geom1").create("c1", "Circle");
model.component("comp1").geom("geom1").feature("c1").set("type", "curve");
model.component("comp1").geom("geom1").feature("c1").set("layername", new String[]{"Layer 1"});
model.component("comp1").geom("geom1").feature("c1").setIndex("layer", "r_o-r_i", 0);
model.component("comp1").geom("geom1").feature("c1").set("r", "r_o");
model.component("comp1").geom("geom1").run();

model.component("comp1").selection().create("sel1", "Explicit");
model.component("comp1").selection("sel1").set(1, 2, 3, 4);
model.component("comp1").selection().create("sel2", "Explicit");
model.component("comp1").selection("sel2").geom("geom1", 1);
model.component("comp1").selection("sel2").set(5, 6, 9, 12);
model.component("comp1").selection().create("sel3", "Explicit");
model.component("comp1").selection("sel3").geom("geom1", 1);
model.component("comp1").selection("sel3").set(1);
model.component("comp1").selection().create("sel4", "Explicit");
model.component("comp1").selection("sel4").geom("geom1", 1);
model.component("comp1").selection("sel4").set(5, 6, 9, 12);
model.component("comp1").selection("sel1").label("Dielectric");
model.component("comp1").selection("sel2").label("OuterConductor");
model.component("comp1").selection("sel3").label("IntPathRadial");
model.component("comp1").selection("sel4").label("IntPathCirc");

model.component("comp1").variable().create("var1");
model.component("comp1").variable("var1").set("V", "int_rad(-emw.Ex*t1x-emw.Ey*t1y)", "Voltage");
model.component("comp1").variable("var1").set("I", "-int_circ(emw.Hx*t1x+emw.Hy*t1y)", "Current");
model.component("comp1").variable("var1").set("Z0_model", "V/I", "Characteristic impedance");

model.component("comp1").material().create("mat1", "Common");
model.component("comp1").material("mat1").selection().named("sel1");

model.component("comp1").cpl().create("intop1", "Integration");
model.component("comp1").cpl().create("intop2", "Integration");
model.component("comp1").cpl("intop1").selection().named("sel3");
model.component("comp1").cpl("intop2").selection().named("sel4");
model.component("comp1").cpl("intop1").set("opname", "int_rad");
model.component("comp1").cpl("intop2").set("opname", "int_circ");

model.component("comp1").physics().create("emw", "ElectromagneticWaves", "geom1");

model.result().table().create("tbl1", "Table");
model.result().table("tbl1").comments("Global Evaluation 1");

model.component("comp1").material("mat1").label("Insulator");
model.component("comp1").material("mat1").propertyGroup("def").set("relpermittivity", new String[]{"eps_r", "0", "0", "0", "eps_r", "0", "0", "0", "eps_r"});
model.component("comp1").material("mat1").propertyGroup("def").set("relpermeability", new String[]{"1", "0", "0", "0", "1", "0", "0", "0", "1"});
model.component("comp1").material("mat1").propertyGroup("def").set("electricconductivity", new String[]{"0", "0", "0", "0", "0", "0", "0", "0", "0"});


model.component("comp1").mesh("mesh1").contribute("physics/emw", false);

model.study().create("std1");
model.study("std1").create("mode", "ModeAnalysis");
model.study("std1").feature("mode").set("neigsactive", true);
model.study("std1").feature("mode").set("neigs", 1);
model.study("std1").feature("mode").set("shift", "sqrt(eps_r)");

model.sol().create("sol1");
model.sol("sol1").study("std1");
model.sol("sol1").attach("std1");
model.sol("sol1").create("st1", "StudyStep");
model.sol("sol1").create("v1", "Variables");
model.sol("sol1").create("e1", "Eigenvalue");
model.sol("sol1").feature("e1").create("d1", "Direct");

model.result().numerical().create("gev1", "EvalGlobal");
model.result().numerical("gev1").set("probetag", "none");
model.result().create("pg1", "PlotGroup2D");
model.result("pg1").create("surf1", "Surface");


model.sol("sol1").attach("std1");
model.sol("sol1").feature("st1").label("Compile Equations: Mode Analysis");
model.sol("sol1").feature("v1").label("Dependent Variables 1.1");
model.sol("sol1").feature("e1").label("Eigenvalue Solver 1.1");
model.sol("sol1").feature("e1").set("transform", "effective_mode_index");
model.sol("sol1").feature("e1").set("neigs", 1);
model.sol("sol1").feature("e1").set("shift", "sqrt(eps_r)");
model.sol("sol1").feature("e1").feature("dDef").label("Direct 2");
model.sol("sol1").feature("e1").feature("aDef").label("Advanced 1");
model.sol("sol1").feature("e1").feature("aDef").set("complexfun", true);
model.sol("sol1").feature("e1").feature("d1").label("Suggested Direct Solver (emw)");
model.sol("sol1").feature("e1").feature("d1").set("linsolver", "pardiso");
model.sol("sol1").runAll();

model.result().numerical("gev1").set("table", "tbl1");
model.result().numerical("gev1").set("expr", new String[]{"Z0_model"});
model.result().numerical("gev1").set("unit", new String[]{"Ω"});
model.result().numerical("gev1").set("descr", new String[]{"Characteristic impedance"});
model.result().numerical("gev1").setResult();
model.result("pg1").label("Electric Field (emw)");
model.result("pg1").set("frametype", "spatial");
model.result("pg1").set("showlegendsmaxmin", true);
model.result("pg1").feature("surf1").label("Surface");
model.result("pg1").feature("surf1").set("smooth", "internal");
model.result("pg1").feature("surf1").set("resolution", "normal");
model.result().table("tbl1").save("OUTPUT_PATH/output.txt");
