model.param().set("a", "10", "System Parameter");
model.param().set("b", "8/3", "System Parameter");
model.param().set("c", "28", "System Parameter");
model.param().set("pert", "0", "Initial data perturbation");
model.param().set("TOL", "1e-16", "Solver Tolerance");
model.param().set("Tfinal", "35", "End Time for simulation");

model.component().create("comp1", true);

model.component("comp1").geom().create("geom1", 3);

model.result().table().create("tbl1", "Table");

model.component("comp1").mesh().create("mesh1");

model.component("comp1").physics().create("ge", "GlobalEquations", "geom1");

model.result().table("tbl1").comments("Global Evaluation 1");

model.component("comp1").physics("ge").prop("EquationForm").set("form", "Automatic");
model.component("comp1").physics("ge").feature("ge1").set("name", new String[][]{{"u"}, {"v"}, {"w"}});
model.component("comp1").physics("ge").feature("ge1")
      .set("equation", new String[][]{{"ut-a*(v-u)"}, {"vt-c*u+v+u*w"}, {"wt-u*v+b*w"}});
model.component("comp1").physics("ge").feature("ge1")
      .set("initialValueU", new String[][]{{"-10+pert"}, {"-4.45+pert"}, {"35.1+pert"}});
model.component("comp1").physics("ge").feature("ge1").set("initialValueUt", new int[][]{{0}, {0}, {0}});
model.component("comp1").physics("ge").feature("ge1").set("description", new String[][]{{""}, {""}, {""}});

model.study().create("std1");
model.study("std1").create("param", "Parametric");
model.study("std1").create("time", "Transient");

model.sol().create("sol1");
model.sol("sol1").study("std1");
model.sol("sol1").attach("std1");
model.sol("sol1").create("st1", "StudyStep");
model.sol("sol1").create("v1", "Variables");
model.sol("sol1").create("t1", "Time");
model.sol("sol1").feature("t1").create("fc1", "FullyCoupled");
model.sol("sol1").feature("t1").feature().remove("fcDef");
model.sol().create("sol2");
model.sol("sol2").study("std1");
model.sol("sol2").label("Parametric Solutions 1");

model.batch().create("p1", "Parametric");
model.batch("p1").create("so1", "Solutionseq");
model.batch("p1").study("std1");

model.result().numerical().create("gev1", "EvalGlobal");
model.result().numerical("gev1").set("probetag", "none");


model.study("std1").feature("param").set("sweeptype", "filled");
model.study("std1").feature("param").set("pname", new String[]{"pert"});
model.study("std1").feature("param").set("plistarr", new String[]{"0 1e-11"});
model.study("std1").feature("param").set("punit", new String[]{""});
model.study("std1").feature("time").set("tlist", "range(0,0.01,Tfinal)");
model.study("std1").feature("time").set("usertol", true);
model.study("std1").feature("time").set("rtol", "TOL");

model.sol("sol1").attach("std1");
model.sol("sol1").feature("st1").label("Compile Equations: Time Dependent");
model.sol("sol1").feature("v1").label("Dependent Variables 1.1");
model.sol("sol1").feature("v1").set("clist", new String[]{"range(0,0.01,Tfinal)", "0.035[s]"});
model.sol("sol1").feature("t1").label("Time-Dependent Solver 1.1");
model.sol("sol1").feature("t1").set("tlist", "range(0,0.01,Tfinal)");
model.sol("sol1").feature("t1").set("rtol", "TOL");
model.sol("sol1").feature("t1").set("odesolvertype", "explicit");
model.sol("sol1").feature("t1").set("rkmethod", "dopri5");
model.sol("sol1").feature("t1").feature("dDef").label("Direct 1");
model.sol("sol1").feature("t1").feature("dDef").set("linsolver", "pardiso");
model.sol("sol1").feature("t1").feature("aDef").label("Advanced 1");
model.sol("sol1").feature("t1").feature("fc1").label("Fully Coupled 1.1");
model.sol("sol1").runAll();

model.batch("p1").set("control", "param");
model.batch("p1").set("sweeptype", "filled");
model.batch("p1").set("pname", new String[]{"pert"});
model.batch("p1").set("plistarr", new String[]{"0 1e-11"});
model.batch("p1").set("punit", new String[]{""});
model.batch("p1").set("err", true);
model.batch("p1").feature("so1").set("seq", "sol1");
model.batch("p1").feature("so1").set("psol", "sol2");
model.batch("p1").feature("so1").set("param", new String[]{""pert","0"", ""pert","1E-11""});
model.batch("p1").attach("std1");
model.batch("p1").run();

model.result().numerical("gev1").set("looplevelinput", new String[]{"last"});
model.result().numerical("gev1").set("table", "tbl1");
model.result().numerical("gev1")
      .set("expr", new String[]{"abs(u-withsol('sol2',u,setval(pert,0),setval(t,t)))", "", ""});
model.result().numerical("gev1").set("unit", new String[]{"1", "", ""});
model.result().numerical("gev1").set("descr", new String[]{"", "", ""});
model.result().numerical("gev1").setResult();
model.result().table("tbl1").save("OUTPUT_PATH/output.txt");
