model.component("comp1").geom("geom1").create("pt1", "Point");
model.component("comp1").geom("geom1").feature("pt1").set("p", new double[]{0.6, 0.2});
model.component("comp1").geom("geom1").run();

model.component("comp1").selection().create("sel1", "Explicit");
model.component("comp1").selection("sel1").geom("geom1", 1);
model.component("comp1").selection("sel1").set(1);
model.component("comp1").selection().create("sel2", "Explicit");
model.component("comp1").selection("sel2").geom("geom1", 1);
model.component("comp1").selection("sel2").set(2);
model.component("comp1").selection().create("sel3", "Explicit");
model.component("comp1").selection("sel3").geom("geom1", 1);
model.component("comp1").selection("sel3").set(4, 5);
model.component("comp1").selection().create("sel4", "Explicit");
model.component("comp1").selection("sel4").geom("geom1", 1);
model.component("comp1").selection("sel4").set(3);
model.component("comp1").selection("sel1").label("Left");
model.component("comp1").selection("sel2").label("Bottom");
model.component("comp1").selection("sel3").label("Right");
model.component("comp1").selection("sel4").label("Upper");

model.component("comp1").material().create("mat1", "Common");

model.component("comp1").physics().create("ht", "HeatTransfer", "geom1");
model.component("comp1").physics("ht").create("temp1", "TemperatureBoundary", 1);
model.component("comp1").physics("ht").feature("temp1").selection().named("sel2");
model.component("comp1").physics("ht").create("hf1", "HeatFluxBoundary", 1);
model.component("comp1").physics("ht").feature("hf1").selection().named("sel3");
model.component("comp1").physics("ht").create("hf2", "HeatFluxBoundary", 1);
model.component("comp1").physics("ht").feature("hf2").selection().named("sel4");

model.component("comp1").mesh("mesh1").create("map1", "Map");

model.result().table("tbl1").comments("Point Evaluation 1");

model.component("comp1").material("mat1").propertyGroup("def")
      .set("thermalconductivity", new String[]{"52", "0", "0", "0", "52", "0", "0", "0", "52"});

model.component("comp1").physics("ht").feature("solid1").set("k_mat", "userdef");
model.component("comp1").physics("ht").feature("solid1")
      .set("k", new int[][]{{52}, {0}, {0}, {0}, {52}, {0}, {0}, {0}, {52}});
model.component("comp1").physics("ht").feature("temp1").set("T0", "100 [degC]");
model.component("comp1").physics("ht").feature("hf1").set("HeatFluxType", "ConvectiveHeatFlux");
model.component("comp1").physics("ht").feature("hf1").set("h", 750);
model.component("comp1").physics("ht").feature("hf1").set("Text", "0 [degC]");
