model.component().create("comp1", true);
model.component("comp1").geom().create("geom1", 2);

model.result().table().create("tbl1", "Table");

model.component("comp1").geom("geom1").axisymmetric(true);

model.component("comp1").mesh().create("mesh1");

model.component("comp1").geom("geom1").create("r1", "Rectangle");
model.component("comp1").geom("geom1").feature("r1").set("size", new double[]{0.3, 0.4});
model.component("comp1").geom("geom1").run();


model.component("comp1").physics().create("ht", "HeatTransfer", "geom1");
model.component("comp1").physics("ht").create("temp1", "TemperatureBoundary", 1);
model.component("comp1").physics("ht").feature("temp1").selection().all();

model.result().table("tbl1").comments("Point Evaluation 1");

model.component("comp1").physics("ht").feature("solid1").set("k_mat", "userdef");
model.component("comp1").physics("ht").feature("solid1")
      .set("k", new int[][]{{52}, {0}, {0}, {0}, {52}, {0}, {0}, {0}, {52}});
model.component("comp1").physics("ht").feature("solid1").set("rho_mat", "userdef");
model.component("comp1").physics("ht").feature("solid1").set("rho", 7850);
model.component("comp1").physics("ht").feature("solid1").set("Cp_mat", "userdef");
model.component("comp1").physics("ht").feature("solid1").set("Cp", 460);
model.component("comp1").physics("ht").feature("init1").set("Tinit", "0[degC]");
model.component("comp1").physics("ht").feature("temp1").set("T0", "1000[degC]");

model.study().create("std1");
model.study("std1").create("time", "Transient");

model.sol().create("sol1");
model.sol("sol1").study("std1");
model.sol("sol1").attach("std1");
model.sol("sol1").create("st1", "StudyStep");
model.sol("sol1").create("v1", "Variables");
model.sol("sol1").create("t1", "Time");
model.sol("sol1").feature("t1").create("fc1", "FullyCoupled");
model.sol("sol1").feature("t1").create("d1", "Direct");
model.sol("sol1").feature("t1").create("i1", "Iterative");
model.sol("sol1").feature("t1").feature("i1").create("mg1", "Multigrid");
model.sol("sol1").feature("t1").feature("i1").feature("mg1").feature("pr").create("so1", "SOR");
model.sol("sol1").feature("t1").feature("i1").feature("mg1").feature("po").create("so1", "SOR");
model.sol("sol1").feature("t1").feature("i1").feature("mg1").feature("cs").create("d1", "Direct");
model.sol("sol1").feature("t1").feature().remove("fcDef");

model.result().dataset().create("rev1", "Revolve2D");
model.result().dataset().create("cpt1", "CutPoint2D");
model.result().numerical().create("pev1", "EvalPoint");
model.result().numerical("pev1").set("probetag", "none");

model.study("std1").feature("time").label("Time Dependent - Continuous Simulation (with Heating)");
model.study("std1").feature("time").set("tlist", "range(0,10,190)");
model.study("std1").feature("time").set("usertol", true);
model.study("std1").feature("time").set("rtol", "1e-5");

model.sol("sol1").attach("std1");
model.sol("sol1").feature("st1")
      .label("Compile Equations: Time Dependent - Continuous Simulation (with Heating)");
model.sol("sol1").feature("v1").label("Dependent Variables 1.1");
model.sol("sol1").feature("v1").set("clist", new String[]{"range(0,10,190)", "0.19[s]"});
model.sol("sol1").feature("t1").label("Time-Dependent Solver 1.1");
model.sol("sol1").feature("t1").set("tlist", "range(0,10,190)");
model.sol("sol1").feature("t1").set("rtol", "1e-5");
model.sol("sol1").feature("t1").set("maxorder", 2);
model.sol("sol1").feature("t1").set("estrat", "exclude");
model.sol("sol1").feature("t1").feature("dDef").label("Direct 2");
model.sol("sol1").feature("t1").feature("aDef").label("Advanced 1");
model.sol("sol1").feature("t1").feature("fc1").label("Fully Coupled 1.1");
model.sol("sol1").feature("t1").feature("fc1").set("linsolver", "d1");
model.sol("sol1").feature("t1").feature("fc1").set("damp", "0.9");
model.sol("sol1").feature("t1").feature("fc1").set("jtech", "once");
model.sol("sol1").feature("t1").feature("fc1").set("stabacc", "aacc");
model.sol("sol1").feature("t1").feature("fc1").set("aaccdim", 5);
model.sol("sol1").feature("t1").feature("fc1").set("aaccmix", 0.9);
model.sol("sol1").feature("t1").feature("fc1").set("aaccdelay", 1);
model.sol("sol1").feature("t1").feature("d1").label("Direct, heat transfer variables (ht)");
model.sol("sol1").feature("t1").feature("d1").set("linsolver", "pardiso");
model.sol("sol1").feature("t1").feature("d1").set("pivotperturb", 1.0E-13);
model.sol("sol1").feature("t1").feature("i1").label("AMG, heat transfer variables (ht)");
model.sol("sol1").feature("t1").feature("i1").set("rhob", 20);
model.sol("sol1").feature("t1").feature("i1").feature("ilDef").label("Incomplete LU 1");
model.sol("sol1").feature("t1").feature("i1").feature("mg1").label("Multigrid 1.1");
model.sol("sol1").feature("t1").feature("i1").feature("mg1").feature("cs").feature("d1")
      .set("linsolver", "pardiso");
model.sol("sol1").feature("t1").feature("i1").feature("mg1").feature("cs").feature("d1")
      .set("pivotperturb", 1.0E-13);
model.sol("sol1").runAll();

model.result().dataset("rev1").label("Revolution 2D");
model.result().dataset("rev1").set("startangle", -90);
model.result().dataset("rev1").set("revangle", 225);
model.result().dataset("cpt1").label("Cut Point 2D - Continuous Heating");
model.result().dataset("cpt1").set("pointx", 0.1);
model.result().dataset("cpt1").set("pointy", 0.3);
model.result().numerical("pev1").set("data", "cpt1");
model.result().numerical("pev1").set("looplevelinput", new String[]{"manual"});
model.result().numerical("pev1").set("looplevel", new int[]{20});
model.result().numerical("pev1").set("table", "tbl1");
model.result().numerical("pev1").set("unit", new String[]{"degC"});
model.result().numerical("pev1").setResult();
model.result().table("tbl1").save("OUTPUT_PATH/output.txt");
