model.component().create("comp1", true);
model.param().set("m", "me_const[1/kg]/e_const[1/C]", "Electron mass (eV/c^2)");
model.param().set("hbar", "hbar_const[1/(J*s)]/e_const[1/C]", "Reduced Planck constant (eV*s)");
model.param().set("V_In", "0", "Potential barrier, InAs (eV)");
model.param().set("V_Ga", "0.697", "Potential barrier, GaAs (eV)");
model.param().set("c_In", "hbar^2/(2*0.023*m)", "c coefficient, InAs");
model.param().set("c_Ga", "hbar^2/(2*0.067*m)", "c coefficient, GaAs");
model.param().set("l", "0", "Principal quantum number");

model.component("comp1").geom().create("geom1", 2);

model.result().table().create("tbl1", "Table");

model.component("comp1").geom("geom1").axisymmetric(true);

model.component("comp1").mesh().create("mesh1");

model.component("comp1").geom("geom1").lengthUnit("nm");
model.component("comp1").geom("geom1").create("r1", "Rectangle");
model.component("comp1").geom("geom1").feature("r1").set("pos", new double[]{12.5, 0});
model.component("comp1").geom("geom1").feature("r1").set("base", "center");
model.component("comp1").geom("geom1").feature("r1").set("size", new int[]{25, 100});
model.component("comp1").geom("geom1").create("r2", "Rectangle");
model.component("comp1").geom("geom1").feature("r2").set("pos", new double[]{12.5, 0});
model.component("comp1").geom("geom1").feature("r2").set("base", "center");
model.component("comp1").geom("geom1").feature("r2").set("size", new int[]{25, 2});
model.component("comp1").geom("geom1").create("pol1", "Polygon");
model.component("comp1").geom("geom1").feature("pol1").set("source", "table");
model.component("comp1").geom("geom1").feature("pol1").set("table", new double[][]{{0, 0}, {12, 0}, {0, 3.6}});
model.component("comp1").geom("geom1").create("co1", "Compose");
model.component("comp1").geom("geom1").feature("co1").set("intbnd", false);
model.component("comp1").geom("geom1").feature("co1").set("formula", "r2+pol1");
model.component("comp1").geom("geom1").create("co2", "Compose");
model.component("comp1").geom("geom1").feature("co2").set("formula", "r1+co1");
model.component("comp1").geom("geom1").run();

model.component("comp1").physics().create("c", "CoefficientFormPDE", "geom1");
model.component("comp1").physics("c").create("cfeq2", "CoefficientFormPDE", 2);
model.component("comp1").physics("c").feature("cfeq2").selection().set(1, 3);
model.component("comp1").physics("c").create("dir1", "DirichletBoundary", 1);
model.component("comp1").physics("c").feature("dir1").selection().set(2, 9);

model.result().table("tbl1").comments("Global Evaluation 1");

model.component("comp1").physics("c").feature("cfeq1").set("c", new String[][]{{"c_In", "0", "0", "c_In"}});
model.component("comp1").physics("c").feature("cfeq1").set("a", "c_In*(l/r)^2+V_In");
model.component("comp1").physics("c").feature("cfeq1").set("be", new String[][]{{"-c_In/r", "0"}});
model.component("comp1").physics("c").feature("cfeq2").set("c", new String[][]{{"c_Ga", "0", "0", "c_Ga"}});
model.component("comp1").physics("c").feature("cfeq2").set("a", "c_Ga*(l/r)^2+V_Ga");
model.component("comp1").physics("c").feature("cfeq2").set("be", new String[][]{{"-c_Ga/r", "0"}});

model.study().create("std1");
model.study("std1").create("eigv", "Eigenvalue");
model.study("std1").feature("eigv").set("neigs", 1);
model.study("std1").feature("eigv").set("neigsactive", true);
model.study("std1").feature("eigv").set("shiftactive", true);
model.study("std1").run();

model.result().dataset().create("rev1", "Revolve2D");
model.result().numerical().create("gev1", "EvalGlobal");
model.result().numerical("gev1").set("probetag", "none");


model.result().dataset("rev1").set("startangle", -90);
model.result().dataset("rev1").set("revangle", 225);
model.result().numerical("gev1").set("looplevelinput", new String[]{"first"});
model.result().numerical("gev1").set("table", "tbl1");
model.result().numerical("gev1").set("expr", new String[]{"lambda"});
model.result().numerical("gev1").set("unit", new String[]{""});
model.result().numerical("gev1").set("descr", new String[]{"Eigenvalue"});
model.result().numerical("gev1").setResult();
model.result().table("tbl1").save("OUTPUT_PATH/output.txt");
